def createTables(mycursor):
    mycursor.execute("CREATE TABLE IF NOT EXISTS Users (UserID VARCHAR(50) NOT NULL, FirstName VARCHAR(50) NOT NULL, LastName VARCHAR(50) NOT NULL, DateJoined DATE NOT NULL, BirthDate DATE NOT NULL, PRIMARY KEY (UserID));")
    mycursor.execute("CREATE TABLE IF NOT EXISTS GroupInfo (GroupID INT NOT NULL, GroupName VARCHAR(250) NOT NULL, PRIMARY KEY (GroupID));")
    mycursor.execute("CREATE TABLE IF NOT EXISTS Topics ( TopicID INT NOT NULL, TopicName VARCHAR(250) NOT NULL, PRIMARY KEY (TopicID));")
    mycursor.execute("CREATE TABLE IF NOT EXISTS GroupMembers (UserID VARCHAR(50) NOT NULL, GroupID INT NOT NULL, PRIMARY KEY (UserID, GroupID), FOREIGN KEY (UserID) REFERENCES Users(UserID), FOREIGN KEY (GroupID) REFERENCES GroupInfo(GroupID));")
    mycursor.execute("CREATE TABLE IF NOT EXISTS FollowedTopics (UserID VARCHAR(50) NOT NULL, TopicID INT NOT NULL, PRIMARY KEY (UserID, TopicID), FOREIGN KEY (UserID) REFERENCES Users(UserID), FOREIGN KEY (TopicID) REFERENCES Topics(TopicID));")
    mycursor.execute("CREATE TABLE IF NOT EXISTS Friends (UserID VARCHAR(50) NOT NULL, FriendID VARCHAR(50) NOT NULL, PRIMARY KEY (UserID, FriendID), FOREIGN KEY (UserID) REFERENCES Users(UserID), FOREIGN KEY (FriendID) REFERENCES Users(UserID));")
    mycursor.execute("CREATE TABLE IF NOT EXISTS FollowedUsers (UserID VARCHAR(50) NOT NULL, FollowedID VARCHAR(50) NOT NULL, PRIMARY KEY (UserID, FollowedID), FOREIGN KEY (UserID) REFERENCES Users(UserID), FOREIGN KEY (FollowedID) REFERENCES Users(UserID));")
    mycursor.execute("CREATE TABLE IF NOT EXISTS Posts (PostID INT NOT NULL, PostTime DATE NOT NULL, Content VARCHAR(1000) NOT NULL, UserID VARCHAR(50) NOT NULL, GroupID INT, TopicID INT, ParentPost INT, PRIMARY KEY (PostID), FOREIGN KEY (UserID) REFERENCES Users(UserID), FOREIGN KEY (GroupID) REFERENCES GroupInfo(GroupID), FOREIGN KEY (TopicID) REFERENCES Topics(TopicID), FOREIGN KEY (ParentPost) REFERENCES Posts(PostID));")
    mycursor.execute("CREATE TABLE IF NOT EXISTS Links (Path VARCHAR(250) NOT NULL, PostID INT NOT NULL, PRIMARY KEY (Path, PostID), FOREIGN KEY (PostID) REFERENCES Posts(PostID));")
    mycursor.execute("CREATE TABLE IF NOT EXISTS Reactions (Reaction BOOLEAN NOT NULL, UserID VARCHAR(50) NOT NULL, PostID INT NOT NULL, PRIMARY KEY (UserID, PostID), FOREIGN KEY (UserID) REFERENCES Users(UserID), FOREIGN KEY (PostID) REFERENCES Posts(PostID));")
    mycursor.execute("CREATE TABLE IF NOT EXISTS ReadStatus (HasRead BOOLEAN NOT NULL, UserID VARCHAR(50) NOT NULL, PostID INT NOT NULL, PRIMARY KEY (UserID, PostID), FOREIGN KEY (UserID) REFERENCES Users(UserID), FOREIGN KEY (PostID) REFERENCES Posts(PostID));")
    return 0
